import React, { useEffect, useState } from 'react';
import Update from "./Update";

function List(){

    const [updateMode, setUpdateMode] = useState(false);   
    
    const [filterMode, setFilterMode] = useState(false);

    const [searchMode, setSearchMode] = useState(false);

    const [query, setQuery] = useState("");

    const [taskToUpdate, setTaskToUpdate] = useState({
        todo_title:"",
        due_date:"",
        todo_tag:"",
        completed:false
    });

    const [listOfTasks, setListOfTasks] = useState([]);

    const [listOfFilteredTasks, setListOfFilteredTasks] = useState([]);
    

    function filterTasks(event){
        setFilterMode(true);
        if (event.target.value === "") {
            setListOfFilteredTasks(listOfTasks);
        } else {
            const filtered = listOfTasks.filter(item => {
                return item.todo_tag.toUpperCase() === event.target.value.toUpperCase();
            });
            setListOfFilteredTasks(filtered);
        }
    }

    function getSearchResults(){
        const filtered = listOfTasks.filter(item => {
            return listOfTasks.includes(item);
        }
            );
        setListOfFilteredTasks(filtered);
    }

    const getTasks = async () => {
        const response = await fetch("http://localhost:3000/todos");
        const data = await response.json();
        setListOfTasks(data);
    }

    function selectUpdate(taskItem) {
        setTaskToUpdate(taskItem);
        setUpdateMode(true);
    }

    
    const createComplete = async taskItem => {
        console.log(new Date(taskItem.due_date));
        const initial = taskItem.completed;
        const completed_task = {
            ...taskItem,
            completed: !initial
        }
        const response = await fetch("http://localhost:3000/todos/" + taskItem.id, {
            method: "put",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(completed_task),
        });

        await getTasks();
    }

    function listTask(taskItem){
        return (
            <div>
                <h3 style={ taskItem.completed ? {textDecoration: "line-through"} : {textDecoration: "none"}}>{taskItem.todo_title}</h3>
                <input type="checkbox" onClick={() => createComplete(taskItem)} />
                <p>Complete</p>
                <h5>{taskItem.due_date}</h5>
                <h5>{taskItem.todo_tag}</h5>
                <p>{taskItem.completed}</p>
                <button onClick={() => deleteTask(taskItem)}>Delete</button>
                <button onClick={() => selectUpdate(taskItem)}>Update</button>
            </div>
        )
    }

    function sortDate(listOfTasks){
        listOfTasks.sort((a, b) => {
            if (new Date(a.due_date) < new Date(b.due_date)){
                return -1;
            } else {
                return 1;
            }
        });
        return listOfTasks;
    }

    const deleteTask = async taskItem => {
        
        const response = await fetch("http://localhost:3000/todos/" + taskItem.id, {
            method: "delete",
        })

        await getTasks();

    }

    function handleSearchChange(event){
        setQuery(event.target.value);
    }


    useEffect(() => {getTasks()}, []);


    return(
        <div>
            <form class="d-flex">
                <input class="form-control me-2" type="search" placeholder="Search" aria-label="Search" />
                <button class="btn btn-outline-success" >Search</button>
            </form >
            <div>
                <h1>List of Tasks</h1><br/>
                <h3>Filter By:</h3>
                <select onChange={filterTasks}>
                    <option value="">All</option>
                    <option value="school">School</option>
                    <option value="work">Work</option>
                    <option value="personal">Personal</option>
                    <option value="urgent">Urgent</option>
                    <option value="important">Important</option>
                </select>
            </div>
            
            {filterMode ? sortDate(listOfFilteredTasks).map(listTask) : sortDate(listOfTasks).map(listTask) }

            <Update taskItem={taskToUpdate}
                    getTasks={getTasks} 
                    update_mode={updateMode}
                    setUpdateMode={setUpdateMode}
                    setTaskToUpdate={setTaskToUpdate}  />
        </div>
        
    )
}

export default List;
