import React from "react";

function Search(props){

    const getTasks = async () => {
        const response = await fetch("http://localhost:3000/todos");
        const data = await response.json();
        setListOfTasks(data);
    }

    const createComplete = async taskItem => {
        console.log(new Date(taskItem.due_date));
        const initial = taskItem.completed;
        const completed_task = {
            ...taskItem,
            completed: !initial
        }
        const response = await fetch("http://localhost:3000/todos/" + taskItem.id, {
            method: "put",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(completed_task),
        });

        await getTasks();
    }

    const deleteTask = async taskItem => {
        
        const response = await fetch("http://localhost:3000/todos/" + taskItem.id, {
            method: "delete",
        })

        await getTasks();

    }

    function listTask(taskItem){
        return (
            <div>
                <h3 style={ taskItem.completed ? {textDecoration: "line-through"} : {textDecoration: "none"}}>{taskItem.todo_title}</h3>
                <input type="checkbox" onClick={() => createComplete(taskItem)} />
                <p>Complete</p>
                <h5>{taskItem.due_date}</h5>
                <h5>{taskItem.todo_tag}</h5>
                <p>{taskItem.completed}</p>
                <button onClick={() => deleteTask(taskItem)}>Delete</button>
                {/* <button onClick={() => selectUpdate(taskItem)}>Update</button> */}
            </div>
        )
    }

    return (
        <div>
            {props.searchResults.map(listTask)}
        </div>
    );
}

export default Search;
