import React, { useState } from "react";

function Update(props){

    const [updateToDo, setUpdateToDo] = useState({task: "", due_date: "", completed: false});

    const getToDos = async (toDo) => {
        const response = await fetch("http://localhost:3000/todos/" + toDo.id);
        const data = await response.json();
        return data;
    }

    function handleUpdateToDo(event){
        const { name, value } = event.target;
        
        setUpdateToDo(prevValue => {
            return {
                ...prevValue,
                [name]: value
            }
        })
    }

    const handleUpdate = async event => { //put new toDo object
        event.preventDefault();
        
        const response = await fetch("http://localhost:3000/todos/" + updateToDo.id, {
            method: "put",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(updateToDo),
        });

        await getToDos();

        setUpdateToDo({task: "", due_date: "", completed: false}); //reset to blank
    } 

    return (
        <div>
            <h1>Update Todo</h1>
            <form>
                <input onChange={handleUpdateToDo} type="text" name="task" placeholder={data.task} />
                <input onChange={handleUpdateToDo} type="date" name="due_date" placeholder={data.due_date} />
                <button onClick={handleUpdate}>Update</button>
            </form>
        </div>
    )
}

export default Update;
