import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Update(props){
    // fill the form with original values
    // Allow the user to update the form
    // send it to the Rails API with PUT method
    // set the update mode to false

    const [taskToUpdate, setTaskToUpdate] = useState(props.taskToUpdate);
    const update_mode = props.update_mode;
    function handleChange(event){
        const {name, value} = event.target;

        setTaskToUpdate(prevValue => {
            return {
                ...prevValue,
                [name]: value
            }
        });
    }

    return (
        
        <div>
            {update_mode && 
            <div>
                <h1>Update Task</h1>
                <form>
                    <input onChange={handleChange} type="text" name="todo_title" value={taskToUpdate.todo_title}/><br/><br/>
                    <input onChange={handleChange} type="date" name="due_date" value={taskToUpdate.due_date} /><br/><br/>
                    <select onChange={handleChange} name="todo_tag" value={taskToUpdate.todo_tag}><br/><br/>
                        <option value="" selected disabled hidden>Tags</option>
                        <option value="school">School</option>
                        <option value="work">Work</option>
                        <option value="personal">Personal</option>
                        <option value="urgent">Urgent</option>
                        <option value="important">Important</option>
                    </select><br/><br/>
                    <button><Link to='/'>Update</Link></button>
                </form>
            </div>}
        </div>
        
    )
}

export default Update;
