import React, { useEffect, useState }  from "react";
import { Route, useHistory } from "react-router-dom";
import Update from "./Update";

function App(){
        // States
        const [toDos, setToDos] = useState([]);
        const [newToDo, setNewToDo] = useState({task: "", due_date: "", completed: false}); 

        // Route
        <Route path="/update">
            <Update />
        </Route>
    
        const getToDos = async () => {
            const response = await fetch("http://localhost:3000/todos");
            const current_data = await response.json();
            setToDos(current_data);
        }
    
        function listToDo(toDoItem){
            return(
                <div>
                    <h3>{toDoItem.task}</h3>
                    <p>{toDoItem.due_date}</p>
                    {toDoItem.completed ? <p>Completed</p> : <p>Not Done</p> }
                
                    <button onClick={() => handleDelete(toDoItem)}>Delete</button>
                    <button onClick={() => handleEdit(toDoItem)}>Edit</button>
                </div>
            )
        }

        function handleEdit(toDoItem){
            const history = useHistory();
            history.push('/update');
        }
    
        function handleNewToDo(event){ //when user submits new entry
            const { name, value } = event.target;
    
            setNewToDo(prevValue => {
                return {
                    ...prevValue,
                    [name]: value
                }
            });
        }
    
        const handleCreate = async event => { //post new toDo object
            event.preventDefault();
            
            const response = await fetch("http://localhost:3000/todos", {
                method: "post",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(newToDo),
            });
    
            await getToDos();
    
            setNewToDo({task: "", due_date: "", completed: false}); //reset to blank
        } 
    
        const handleDelete = async toDo => {
            // toDo.preventDefault();
    
            const response = await fetch("http://localhost:3000/todos/" + toDo.id, {
                method: "delete",
            } );
    
            await getToDos();
        }
    
        useEffect(() => { //get toDos when the page loads
            getToDos()
        }, []);
    
        return (
            <div>
                <h1>Tasks to Do</h1>
                {toDos.length > 0 ? toDos.map(listToDo) : <h2>No Tasks</h2>}
                <br/><br/><br/><br/>
    
                <h2>Add Task</h2>
    
                <form>
                    <input type="text" name="task" placeholder="New Task" onChange={handleNewToDo} value={newToDo.task} /><br/><br/>
                    <input type="date" name="due_date" placeholder="Due Date" onChange={handleNewToDo} value={newToDo.due_date} /><br/><br/>
                    <button onClick={handleCreate}>Add</button>
                </form>
            </div>
        );
}

export default ToDoList;
