import React from 'react';

function Search(props){

    const [listOfTasks, setListOfTasks] = useState([]);

    const getTasks = async () => {
        const response = await fetch("http://localhost:3000/todos");
        const data = await response.json();
        setListOfTasks(data);
    }

    function listTask(taskItem){
        return (
            <div>
                <h3 style={ taskItem.completed ? {textDecoration: "line-through"} : {textDecoration: "none"}}>{taskItem.todo_title}</h3>
                <input type="checkbox" onClick={() => createComplete(taskItem)} />
                <p>Complete</p>
                <h5>{taskItem.due_date}</h5>
                <h5>{taskItem.todo_tag}</h5>
                <p>{taskItem.completed}</p>
                {/* <button onClick={() => deleteTask(taskItem)}>Delete</button>
                <button onClick={() => selectUpdate(taskItem)}>Update</button> */}
            </div>
        )
    }
    return (
        <div>
            {props.searchResults.map(listTask)}
        </div>
    );
}

export default Search;
