import React, { useEffect, useState } from "react";

function App(){
    const blank_toDo = {task: "", due_date: "", completed: false};
    const [toDos, setToDos] = useState([]);
    const [input, setInput] = useState(""); //changes based on input
    const [newToDo, setNewToDo] = useState(blank_toDo); 
    const [updateToDo, setUpdateToDo] = useState(blank_toDo);

    const getToDos = async () => {
        const response = await fetch("http://localhost:3000/todos");
        const current_data = await response.json();
        setToDos(current_data);
    }

    function listToDo(toDoItem){
        return(
            <div>
                <h3>{toDoItem.task}</h3>
                <p>{toDoItem.due_date}</p>
                {toDoItem.completed ? <p>Completed</p> : <p>Not Done</p> }
            </div>
        )
    }

    function handleChange(event){
        setInput(event.target.value);
    }

    function handleNewToDo(event){ //when user submits new entry
        const { name, value } = event.target;

        setNewToDo(prevValue => {
            return {
                ...prevValue,
                [name]: value
            }
        });
    }

    const handleCreate = async event => { //post new toDo object
        event.preventDefault();
        
        const response = await fetch("http://localhost:3000/todos", {
            method: "post",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(newToDo),
        });

        await getToDos();

        setNewToDo(blank_toDo); //reset to blank
    } 

    function handleUpdateToDo(event){
        const { name, value } = event.target;
        
        setUpdateToDo(prevValue => {
            return {
                ...prevValue,
                [name]: value
            }
        })
    }

    const handleUpdate = async event => { //put new toDo object
        event.preventDefault();
        
        const response = await fetch("http://localhost:3000/todos/" + updateToDo.id, {
            method: "put",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(updateToDo),
        });

        await getToDos();

        setUpdateToDo(blank_toDo); //reset to blank
    } 

    const handleDelete = async toDo => {
        // toDo.preventDefault();

        const response = await fetch("http://localhost:3000/todos/" + toDo.id, {
            method: "delete",
        } );

        await getToDos();
    }

    useEffect(() => { //get toDos when the page loads
        getToDos()
    }, []);

    return (
        <div>
            <h1>Tasks to Do</h1>
            {toDos.length > 0 ? toDos.map(listToDo) : <h2>No Tasks</h2>}
            <br/><br/>
            <form>
                <input type="text" placeholder="New Task" /><br/>
                <input type="date" placeholder="Due Date" /><br/>
                <button>Add</button>
            </form>
        </div>
    );
}

export default App;
