import React, { useEffect, useState } from 'react';
import Update from "./Update";

function List(){
    // Data for updating
    let update_mode = false;
    const [taskToUpdate, setTaskToUpdate] = useState({
        todo_title:"",
        due_date:"",
        todo_tag:"",
        completed:false
    });
    
    const [listOfTasks, setListOfTasks] = useState([]);

    const getTasks = async () => {
        const response = await fetch("http://localhost:3000/todos");
        const data = await response.json();
        setListOfTasks(data);
    }

    function listTask(taskItem){
        return (
            <div>
                <h3>{taskItem.todo_title}</h3>
                <h5>{taskItem.due_date}</h5>
                <h5>{taskItem.todo_tag}</h5>
                <p>{taskItem.completed}</p>
                <button onClick={() => deleteTask(taskItem)}>Delete</button>
                <button>Update</button>
            </div>
        )
    }

    const deleteTask = async taskItem => {
        console.log(taskItem);
        const response = await fetch("http://localhost:3000/todos/" + taskItem.id, {
            method: "delete",
        })

        await getTasks();

    }

    useEffect(() => {getTasks()}, []);

    return(
        <div>
            <h1>List of Tasks</h1>
            {listOfTasks.map(listTask)}
            {update_mode && <Update  listOfTasks={listOfTasks} getTasks={getTasks} />}
        </div>
        
    )
}

export default List;
