import React, { useEffect, useState }  from "react";

function App(){
        // States
        const [toDos, setToDos] = useState([]);
        const [newToDo, setNewToDo] = useState({task: "", due_date: "", completed: false}); 
        const [updateToDo, setUpdateToDo] = useState({task: "", due_date: "", completed: false});

        let edit_mode = false;
    
        const getToDos = async () => {
            const response = await fetch("http://localhost:3000/todos");
            const current_data = await response.json();
            setToDos(current_data);
        }
    
        function listToDo(toDoItem){
            return(
                <div>
                    <br/>
                    <h5>{toDoItem.task}</h5>
                    <p>{toDoItem.due_date}</p>
                    {toDoItem.completed ? <p>Completed</p> : <p>Not Done</p> }
                
                    <button onClick={() => handleDelete(toDoItem)}>Delete</button>
                    <button onClick={() => firstUpdateToDo(toDoItem)}>Edit</button>
                </div>
            )
        }
    
        function handleNewToDo(event){ //when user submits new entry
            const { name, value } = event.target;
    
            setNewToDo(prevValue => {
                return {
                    ...prevValue,
                    [name]: value
                }
            });
        }
    
        const handleCreate = async event => { //post new toDo object
            event.preventDefault();
            
            const response = await fetch("http://localhost:3000/todos", {
                method: "post",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(newToDo),
            });
    
            await getToDos();
    
            setNewToDo({task: "", due_date: "", completed: false}); //reset to blank
        } 
    
        const handleDelete = async toDo => {
            // toDo.preventDefault();
    
            const response = await fetch("http://localhost:3000/todos/" + toDo.id, {
                method: "delete",
            } );
    
            await getToDos();
        }
        
        const firstUpdateToDo = async toDo => {
            const response = await fetch("http://localhost:3000/todos/" + toDo.id);
            const data = await response.json();
            setUpdateToDo(data);
        }
        
        function handleUpdateToDo(event){
            const { name, value } = event.target;
            setUpdateToDo(prevValue => {
                return {
                    ...prevValue,
                    [name]: value
                }
            })
        }

        const handleUpdate = async event => {
            event.preventDefault();

            const response = await fetch("http://localhost:3000/todos/" + updateToDo.id, {
                method: "put",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(updateToDo),
            });

            await getToDos();
        }
    
        useEffect(() => { //get toDos when the page loads
            getToDos()
        }, []);
    
        return (
            <div>
                <h1>Tasks to Do</h1>
                {toDos.length > 0 ? toDos.map(listToDo) : <h2>No Tasks</h2>}
                <br/><br/><br/><br/>
    
                <h2>Add Task</h2>
    
                <form>
                    <input type="text" name="task" placeholder="New Task" onChange={handleNewToDo} value={newToDo.task} /><br/><br/>
                    <input type="date" name="due_date" placeholder="Due Date" onChange={handleNewToDo} value={newToDo.due_date} /><br/><br/>
                    <button onClick={handleCreate}>Add</button>
                </form>
                { edit_mode && 
                <div>
                    <h2>Edit Task</h2>

                    <form>
                        <input type="text" name="task" placeholder={updateToDo.task} onChange={handleUpdateToDo} value={updateToDo.task} /><br/><br/>
                        <input type="date" name="due_date" placeholder={updateToDo.due_date} onChange={handleUpdateToDo} value={updateToDo.due_date} /><br/><br/>
                        <button onClick={handleUpdate}>Edit</button>
                    </form>
                </div> }
            </div>
        );
}

export default App;
