import React, { useState } from 'react';
import { Link, Route, Switch } from 'react-router-dom';
import Add from "./Add";
import List from "./List";

function Navbar(){

    const [searchMode, setSearchMode] = useState(false);

    const [query, setQuery] = useState("");

    const [searchResults, setSearchResults] = useState([]);

    const [listOfTasks, setListOfTasks] = useState([]);

    function handleSearchChange(event){
        setQuery(event.target.value);
    }

    const getTasks = async () => {
        const response = await fetch("http://localhost:3000/todos");
        const data = await response.json();
        setListOfTasks(data);
    }

    function searchTask(){
        props.setSearchMode(true);
        props.setQuery("");
        if (props.searchQuery === ""){
            setSearchResults(listOfTasks);
        } else {
            const filtered = listOfTasks.filter(item => {
                return item.todo_title.includes(props.searchQuery);
            });
            setSearchResults(filtered);
        }
    }


    return (
        <div>
        <nav class="navbar navbar-expand-lg navbar-dark" style={{backgroundColor: "#7a2323"}}>
        <div class="container-fluid">
            <a class="navbar-brand">Taskify</a>
            <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
            </button>
            <div class="collapse navbar-collapse" id="navbarNav">
            <ul class="navbar-nav">
                <li class="nav-item">
                    <Link to="/" class="nav-link active"><strong>Tasks</strong></Link>
                </li>
                <li class="nav-item">
                    <Link to="/add" class="nav-link active">Add Task</Link>
                </li>
            </ul>
            
            
            </div>
            <form class="d-flex">
                <input onChange={handleSearchChange} class="form-control me-2" type="search" placeholder="Search" aria-label="Search"  />
                <button onClick={() => setSearchMode(true)} class="btn btn-outline-success" >Search</button>
            </form>
            
            
        </div>
        </nav>
        
        <Switch>
            <Route exact path="/" render={()=> <List setQuery={setQuery} searchQuery={query} searchMode={searchMode} setSearchMode={setSearchMode} /> } />
            <Route component={Add} path="/add" />
        </Switch>
    </div>

    )
}

export default Navbar;
