import React, { useState } from 'react';
import { Link, Route, Switch } from 'react-router-dom';
import Add from "./Add";
import List from "./List";
import Search from "./Search";

function Navbar(){

    //Search functionality

    const [query, setQuery] = useState("");
    const [searchResults, setSearchResults] = useState({
        todo_title:"",
        due_date:"",
        completed:false,
        todo_tag:""
    });

    function handleChange(event){
        setQuery(event.target.value);
    }

    const searchTask = async event => {
        event.preventDefault();
        const response = await fetch("http://localhost:3000/search?q=" + query);
        const data = await response.json();
        setSearchResults(data);
    }

    return (
        <div>
        <nav class="navbar navbar-expand-lg navbar-dark" style={{backgroundColor: "#7a2323"}}>
        <div class="container-fluid">
            <a class="navbar-brand">Taskify</a>
            <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
            </button>
            <div class="collapse navbar-collapse" id="navbarNav">
            <ul class="navbar-nav">
                <li class="nav-item">
                    <Link to="/" class="nav-link active"><strong>Tasks</strong></Link>
                </li>
                <li class="nav-item">
                    <Link to="/add" class="nav-link active">Add Task</Link>
                </li>
            </ul>
            
            </div>
            <form class="d-flex">
                <input onChange={handleChange} class="form-control me-2" type="search" aria-label="Search" />
                <button onClick={searchTask} class="btn btn-outline-light">Search</button>

            </form>
            
        </div>
        </nav>
        <Switch>
        <Route component={List} exact path="/" />
        <Route component={Add} path="/add" />
        <Route component={Search} path="/search" />
    </Switch>
    </div>

    )
}

export default Navbar;
