import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Update(props){

    const update_mode = props.update_mode;
    const task_item = props.taskItem;

    // function handleChange(event){
    //     const {name, value} = event.target;

    //     setTaskToUpdate(prevValue => {
    //         return {
    //             ...prevValue,
    //             [name]: value
    //         }
    //     });
    // }

    return (
        
        <div>
            {update_mode && 
            <div>
                <h1>Update Task</h1>
                <form>
                    <input type="text" name="todo_title" value={task_item.todo_title}/><br/><br/>
                    <input type="date" name="due_date" value={task_item.due_date} /><br/><br/>
                    <select name="todo_tag" value={task_item.todo_tag}><br/><br/>
                        <option value="" selected disabled hidden>Tags</option>
                        <option value="school">School</option>
                        <option value="work">Work</option>
                        <option value="personal">Personal</option>
                        <option value="urgent">Urgent</option>
                        <option value="important">Important</option>
                    </select><br/><br/>
                    <button><Link to='/'>Update</Link></button>
                </form>
            </div>}
        </div>
        
    )
}

export default Update;
