import React, { useEffect, useState }  from "react";
import { Route } from "react-router-dom";
import Update from "./Update";

function App(){

    // <Route component={Update} path="/update" />

    const [toDos, setToDos] = useState({task: "", due_date: "", completed: false});

    const getToDos = async () => {
        const response = await fetch("http://localhost:3000/todos");
        const current_data = await response.json();
        setToDos(current_data);
    }

    function listToDo(toDoItem){
        return(
            <div>
                <h3>{toDoItem.task}</h3>
                <p>{toDoItem.due_date}</p>
                {toDoItem.completed ? <p>Completed</p> : <p>Not Done</p> }
            
                <button onClick={() => handleDelete(toDoItem)}>Delete</button>
            </div>
        )
    }

    function handleNewToDo(event){ //when user submits new entry
        const { name, value } = event.target;

        setNewToDo(prevValue => {
            return {
                ...prevValue,
                [name]: value
            }
        });
    }

    const handleCreate = async event => { //post new toDo object
        event.preventDefault();
        
        const response = await fetch("http://localhost:3000/todos", {
            method: "post",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify(newToDo),
        });

